import { Component } from '@angular/core';
import { MessagesService } from '../messages.service';

@Component({
  selector: 'app-add-messages',
  templateUrl: './add-messages.component.html',
  styleUrls: ['./add-messages.component.css']
})
export class AddMessagesComponent {

  constructor(public messagesService: MessagesService){}

  message: string = '';

}
<form>
  <label class="form-label">Ingrese un mensaje</label>
  <input type="text" class="form-control" placeholder="Ingrese un mensaje" name="message" [(ngModel)]="message">
  <button class="btn btn-primary mt-2 mb-4">
    Agregar
  </button>
</form>


