import { Component } from '@angular/core';
import { PorfolioService } from '../servicios/porfolio.service';

@Component({
  selector: 'app-presentacion',
  templateUrl: './presentacion.component.html',
  styleUrls: ['./presentacion.component.css']
})
export class PresentacionComponent {

  miPorfolio:any;

  constructor(private datosPorfolio:PorfolioService){}

  ngOnInit():void{
    this.datosPorfolio.obtenerDatos().subscribe(data =>{ //(8) me suscribo al observable
      console.log(data);

      this.miPorfolio = data; //(9b) asignamos a la variable definida
    });
  }
}
<!-- sección de presentación -->
<section class="presentacion m-3">

<!-- sección del Banner -->
<img class="banner" [src]="miPorfolio.backImage" alt="banner" class="w-100 img-fluid">

<div class="row justify-content-between presentacion_datos">

    <div class="col-6">
      <img [src]="miPorfolio.backImage" alt="foto-perfil" class="fotoperfil rounded-circle img-fluid col-7">
      <h1 class="nombre">{{miPorfolio.name}}</h1>
      <h4>{{miPorfolio.position1}}</h4>
      <h4>{{miPorfolio.position2}}</h4>
      <br><p>{{miPorfolio.ubication}}<a href="http://">Información de contacto</a></p> <!-- Agregar link -->

      <div>
        <a class="btn btn-primary" href="#" role="button">Tengo interes en</a>
        <button type="button" class="btn btn-secondary mx-2">Añadir Sección</button>
        <button type="button" class="btn btn-secondary">Más</button>
      </div>
    </div>

    <div class="col-5 instituciones">
      <img class="logo" src="/assets/logo_UNER.png" alt="logo UNER">
      <h4>Universidad Nacional de Entre Ríos</h4> <br>
      <img class="logo" src="/assets/logo_Arg_Prog.jpg" alt="Logo Argentina Programa">
      <h4>Argentina programa</h4>
    </div>

</div>
</section>
