import { Component, OnInit } from '@angular/core';
import { PorfolioService } from '../servicios/porfolio.service'
import { persona } from '../../model/persona.model';
//import { PersonaService } from 'src/app/service/persona.service';

@Component({
  selector: 'app-acercade',
  templateUrl: './acercade.component.html',
  styleUrls: ['./acercade.component.css']
})
export class AcercadeComponent implements OnInit {
  persona: persona = new persona("","","");

  //constructor(public personaService: PersonaService){}

  miPorfolio: any;

  constructor(private datosPorfolio:PorfolioService){}

  ngOnInit():void{
    this.datosPorfolio.obtenerDatos().subscribe(data =>{
      console.log(data);
      this.miPorfolio = data;
      });
    //this.personaService.getPersona().subscribe(data => {this.persona = data});
  }
}
<div class="seccion">
    <div class="row g-0">
        <div class="col-md-11">
          <div class="card-body txt">
          <h2 class="fuente1 card-title display-5 text-center">{{miPorfolio.aboutTitle}}</h2>
        </div>
        
    </div>

    <h6 class="card-text display-6 text-center ">{{persona.descripcion}}</h6>
    
</div>
<div class="col-md-1" id="quiensoyeditar">
  <img src="/assets/img/iconos/agregar.svg" class="btn btn-outline-warning btn-outline-info btn-floating m-1" href=""
      role="button">
</div>
