import { Component } from '@angular/core';
import { PorfolioService } from '../servicios/porfolio.service'

@Component({
  selector: 'app-acercade',
  templateUrl: './acercade.component.html',
  styleUrls: ['./acercade.component.css']
})
export class AcercadeComponent {

  miPorfolio: any;

  constructor(private datosPorfolio:PorfolioService){}

  ngOnInit():void{
    this.datosPorfolio.obtenerDatos().subscribe(data =>{
      console.log(data);
      this.miPorfolio = data;
    });
  }
}


<div class="seccion">
    <div class="row g-0">
        <div class="col-md-11">
          <div class="card-body txt">
          <h2 class="fuente1 card-title display-5 text-center">{{miPorfolio.aboutTitle}}</h2>
        </div>
        <div class="col-2 text-center">
          <button type="button" class="btn btn-light">
            <i class="fa-solid fa-pen-to-square"></i>
          </button>
        </div>
    </div>

    <h6 class="card-text display-6 text-center ">{{miPorfolio.about}}</h6>
</div>
<div class="col-md-1" id="quiensoyeditar">
  <img src="/assets/img/iconos/editar.svg" class="btn btn-light btn-outline-info btn-floating m-1" href=""
      role="button">
</div>
